import { Component } from '@angular/core';
import { RemoteService, TestModel } from '../remote.service';

@Component({
  selector: 'app-view',
  templateUrl: './view.component.html',
  styleUrls: ['./view.component.css']
})
export class ViewComponent {
  remote: RemoteService;
  testModels: TestModel[] = [];

  constructor(remoteService: RemoteService) {
    this.remote = remoteService;
    this.remote.get().subscribe();
  }

  get() {
    this.remote.get().subscribe({
      next: (value) => {
        console.log('retrieved models: ');
        this.testModels = value;
      },
      error: (error) => {/*do something*/},
      complete: () => {/*do something*/}
    });
  }

}
